package com.tnsif.day3;

public class ForEachloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float a[]= {10.3f,20.3f,30.1f,40.3f,50.1f};
		for(float i:a) {
			System.out.println(i);
		}
		char ch[]= {'j','a','v','a'};
		for(char c:ch) {
			System.out.print(c);
		}
		
		

	}

}
